function updateTheme(){const e=(new Date).getHours();document.body.className=e>=8&&e<17?"light-mode":"dark-mode"}function updateTimes(){const e=new Date,t=e.toLocaleTimeString("en-US",{hour:"numeric",minute:"2-digit",hour12:!0,timeZone:"America/Chicago"}),[n,o]=t.split(" ");document.getElementById("localTime").textContent=n,document.getElementById("localPeriod").textContent=" "+o;const a=e.toLocaleTimeString("en-US",{hour:"numeric",minute:"2-digit",hour12:!0,timeZone:"Europe/Sofia"}),[i,m]=a.split(" ");document.getElementById("sofiaTime").textContent=i,document.getElementById("sofiaPeriod").textContent=" "+m;const r=`${e.getDate()} ${e.toLocaleDateString("en-US",{month:"short"})}`;document.getElementById("date").textContent=r}async function updateWeather(){try{const e=30.4515,t=-91.1871,n=await fetch(`https://api.open-meteo.com/v1/forecast?latitude=${e}&longitude=${t}&current=temperature_2m&temperature_unit=fahrenheit&timezone=auto`);if(!n.ok)throw new Error("Weather API request failed");const o=await n.json(),a=Math.round(o.current.temperature_2m),i=Math.round(5*(a-32)/9);document.getElementById("tempF").textContent=`${a}`,document.getElementById("tempC").textContent=`${i}`}catch(e){console.error("Error fetching weather:",e),document.getElementById("tempF").textContent="--",document.getElementById("tempC").textContent="--"}}function init(){updateTimes(),updateWeather(),setInterval(updateTimes,1e3),setInterval(updateTheme,6e4),setInterval(updateWeather,9e5)}"loading"===document.readyState?document.addEventListener("DOMContentLoaded",init):init();